import "./NewChat.css";
import "../../index.css";
// import {user} from "../Join/NewJoin";
import socketIo from "socket.io-client";
import React, { useEffect } from 'react'
import { FaUser, FaUserCircle } from "react-icons/fa";
import { HiOutlineSearch } from "react-icons/hi";

const ENDPOINT = "http://localhost:5000";

function NewChat() {
  
  useEffect(() => {
    const socket = socketIo(ENDPOINT, {transports: ["websocket"]});
    socket.on("connect",()=>{
        // alert(`Abe aagya tu ${user}?`)
        console.log("New user aya!");
    })
  
    return () => {
    }
  },)
  

  return (
  <div className='parent-cont-chat'>
    <div className="main-cont-chat">
      
      {/* HEADER */}
      <div className="navbar-chat">
        
        <div className="left-logo-cont">
          <h1 className="pokit-logo">
            Pokit
          </h1>
          <h4 className="meta">
            Anonymous Chat App
          </h4>
        </div>
        
        <div className="my-user">
          <div className="name-status-con">
            <h2 className="my-name">Swastik</h2>
            <span className="status">Online</span>
          </div>
          {<FaUser className="user-icon"/>}
        </div>

      </div>

    <hr />

    {/* CHAT BODY */}
    <div className="body-cont">
      <div className="chats-cont">
        <div className="search-bar">
          {<HiOutlineSearch className="search-icon"/>}

        </div>
      </div>
      <div className="chat-window">
         <div className="chat-window-header">
          {<FaUserCircle className="current-user-icon"/>}
          <div className="convo-tag">Conversation with 4n5hu</div>
        </div>
      </div>
    </div>
    
    <footer className="github-cont">
      <div className="github">github</div>
    </footer>
    </div>
  </div>
  )
}

export default NewChat
