import React from 'react'
import {Link} from "react-router-dom";

let user;

const getchats=()=>{
    user=document.getElementById("username").value;
    if(user!==""){
        console.log(`${user} Have Entered The Chat Section!`);
    }else{
        alert("Please enter an alias to enter the chat Section!")
        console.log("No name entered!");
    }
}

const getchatskey=(e)=>{
  if (e.keyCode === 13) {
    getchats();
  }
};


function NewJoin() {
  return (
    <div className="main-cont">
        <div className="title-cont">
            <h1>Pokit</h1>
        </div>
        <div className="login-cont">
            <h3>Login</h3>
        </div>
        <div className="input-cont">
            <input onKeyDown={getchatskey} placeholder='Type your alias...' type="text" id="username"/>
        </div>
        <div className="btn-cont">
            <Link to="/chat">
                <button onClick={getchats}>Get me in!</button>
            </Link>
        </div>
    </div>
  )
}

export default NewJoin
export {user}
