import React from 'react'
import {Link} from "react-router-dom";
import "./NewJoin.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

var user;

const displaytoast = () => {
    toast.info('Please Provide an Alias', {
        position: "bottom-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "4n5hu",
        });
};

const getchats=()=>{
    user=document.getElementById("username").value;
    user=document.getElementById("username").value = "";
    if(user!==""){
        console.log(`${user} Have Entered The Chat Section!`);
    }else{
        // alert("Please enter an alias to enter the chat Section!")
        displaytoast()
        console.log("No name entered!");
    }
}

function NewJoin() {

    return (
    <div className="parent-cont">
        <ToastContainer
            position="bottom-right"
            autoClose={2000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
            theme="4n5hu"
            />
        <div className="main-cont">
                <div className="title-cont">
                <div>Pokit.</div>
            </div>
            <div className="login-cont">
                <div>Login</div>
            </div>
            <div className="input-cont">
                <input placeholder='Type your alias' type="text" id="username"/>
            </div>
            <div className="btn-cont">
                <Link to="/chat">
                    <button onClick={getchats}>Chat Now&nbsp; <span>&#8594;</span></button>
                </Link>
            </div>
        </div>
    </div>
  )
}

export default NewJoin
export {user}
